fn test_local_tx() {
    let _ = tracing_subscriber::fmt::try_init();
    //commit tx
    let btx_tx_hex = "";
    decode_inscription(btx_tx_hex);
    //reveal tx
    let btc_tx_hex = "";
    decode_inscription(btc_tx_hex);
}
